package com.practice.start;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//operator are symbol that performs operations and value
		//1-arithematic operators:used to perform basic math oepration
		//Addition operator:
		int a = 10, b = 5;
		System.out.println("a + b = " + (a + b));
		
		//Subtraction operator
		 System.out.println("a - b = " + (a - b));
		 
		 //MultiplicTion operator
		 System.out.println("a * b = " + (a * b)); 
		 
		 //division operator
		 System.out.println("a / b = " + (a / b));
		 
		 //Modulus (Remainder)
		 System.out.println("a % b = " + (a % b));
		 
		
		
	}

}
