package com.practice.start;

public class DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//datatypes tells us what type of value a variable can hold 
		//there are two types of data type
		//primitive and non primitive
		//primitive : built in data types that store simple value
		//1-integer type: bytes, short,int,longs
		float marks = 2.3f;  //float holds decimal value, it takes 4 bytes and you need to use f or F at the end of value
		System.out.println(marks);
		
		byte num3 =2;  //bytes take 1 byte of memory
		System.out.println(num3);
		
		double num4=2.34; // it takes 8 byte of memory it will rum more decimal numbers 
		System.out.println(num4);
		
		short num5 = 12;  //takes 2 byte
		System.out.println(num5);
		
		long lo =1234l; //in long we will use long inerger value  it takes 8 byte
		
		System.out.println(lo);
		
		
		char n='A' ; //char take 2 bytes ,single charecter
		System.out.println(n);	
		boolean b=true;  //take 1 bit 
		System.out.println(b);
		
		
		

	}

}
