package com.practice.start;

public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//gives the output even is condition is false
		int i=4;
		do {
			System.out.println(i);
			i++;
		}
		while(i<=2);

	}
	
	//if you know how many times you want to run the code then use for loop
	//if you want to run the code until condition is satisfied
	//even if condition is false and you want to run the code we will use do while
	

}
